class Parser:

    def __init__(self, root_url):
        self.root_url = root_url
        self.page = self.get_page(url=self.root_url)

    @staticmethod
    def get_page(url):
        try:
            return BeautifulSoup(requests.get(url).content, 'lxml')
        except InvalidSchema:
            with open(url, 'r', encoding="utf-8") as html_file:
                return BeautifulSoup(html_file.read(), 'lxml')

    def download_html(self, url_path: str = None, save_path: str | Path = None):
        url_path = self.root_url if url_path is None else url_path
        save_path = ROOT_PATH.joinpath("body.html") if save_path is None else save_path
        page = requests.get(url_path)
        with open(save_path, 'wb') as html_file:
            html_file.write(page.content)


class ParserPageProduct(Parser):
    def __init__(self, root_url):
        super().__init__(root_url=root_url)

    @property
    def article_id(self):
        return int(self.page.find(class_="product-page-content__article").text.strip().split(":")[1])

    @property
    def title(self):
        return self.page.find(class_="product-page-content__product-name").text.strip()

    @property
    def data_script(self):
        scr = self.page.find_all("script")
        for d in scr:
            d = d.text
            if d:
                if "window.__NUXT__" in d:
                    print(d)
        return

class ParserMetro(Parser):

    def __init__(self, root_url):
        super().__init__(root_url=root_url.split('?')[0])

    @property
    def pagination_list(self):
        items_pagination = self.page.find(class_="subcategory-or-type__pagination").findAll('li')
        items_pagination = map(lambda x: x.text, items_pagination)
        items_pagination = [int(x) for x in items_pagination if x.isdigit()]
        return list(range(min(items_pagination), max(items_pagination)+1))

    def get_items_for_page(self):
        self.page.findall(class_='product-card__content')


